import "../../public/css/bootstrap.css";

const Body = () => {
  return (
    <section className="body">
      <div className="row">
        <div className="col-3 border">sdsd</div>
        <div className="col-7 border">sdsd</div>
        <div className="col-2 border">sdsd</div>
      </div>
      <div className="top d-flex">
        <div className="left">
          <input
            type="text"
            className="search form-control"
            placeholder="Search for an article"
          />
        </div>
        {/* /.left */}
        <div className="middle">
          <ul className="body-nav list-unstyled f-ap-r d-flex flex-wrap">
            <li className="body-nav-item active">All</li>
            <li className="body-nav-item">Networking</li>
            <li className="body-nav-item">Business</li>
            <li className="body-nav-item">Men&amp;Mice</li>
            <li className="body-nav-item">Events</li>
            <li className="body-nav-item">Courses</li>
          </ul>
          {/* /.body-nav */}
        </div>
        {/* /.moddle */}
        <div className="right">
          <div className="sort">
            <span className="f-cap-r">Sort by: </span>
            <button
              className="btn sort-button f-cap-b"
              type="button"
              id="dropdownMenuButton"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              Newest <img src="/img/caret-down-grey.svg" className="ml-2" />
            </button>
            <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
              <a class="dropdown-item" href="#">
                Action
              </a>
              <a class="dropdown-item" href="#">
                Another action
              </a>
              <a class="dropdown-item" href="#">
                Something else here
              </a>
            </div>
          </div>
        </div>
        {/* /.right */}
      </div>
      {/* /.top */}
      <div className="bottom">.row</div>
      {/* /.bottom */}
      <style jsx>{`
        textarea:hover,
        input:hover,
        textarea:active,
        input:active,
        textarea:focus,
        input:focus,
        button:focus,
        button:active,
        button:hover,
        label:focus,
        .btn:active,
        .btn.active {
          outline: 0px !important;
          -webkit-appearance: none;
          box-shadow: none !important;
        }
        .f-ap-r {
          font-family: "Apercu Regular";
        }
        .f-ap-m {
          font-family: "Apercu Medium";
        }
        .f-ap-b {
          font-family: "Apercu Bold";
        }
        .f-cap-r {
          font-family: "Capitolium Regular";
        }
        .f-cap-b {
          font-family: "Capitolium Bold";
        }
      `}</style>
      <style jsx>{`
        .body .top .left,
        .body .top .middle,
        .body .top .right {
          padding: 46px 0;
          border-right: 1px solid #4e4c4d;
        }
        .body .top .left {
          width: 20%;
        }
        .body .top .middle {
          width: 60%;
          padding-left: 20px;
        }
        .body .top .right {
          width: 20%;
          padding-left: 25px;
          border: none;
        }
        .body .top .left .search {
          font-size: 18px;
          color: #bdbcbc;
          background: url(/img/search.svg) no-repeat transparent;
          background-position: left center;
          border: none;
          outline: none;
          padding-left: 35px;
        }
        .body .top .middle .body-nav-item {
          color: rgba(255, 255, 255, 0.6);
          font-size: 18px;
          margin: 0 20px;
          cursor: pointer;
          position: relative;
        }
        .body .top .middle .body-nav-item.active {
          color: #ffffff;
        }

        .body .top .middle .body-nav-item:after {
          content: "";
          position: absolute;
          left: 0;
          bottom: -5px;
          width: 100%;
          height: 2px;
          background: transparent;
          transition: all 0.5s;
        }
        .body .top .middle .body-nav-item.active:after,
        .body .top .middle .body-nav-item:hover:after {
          background: #ffdb00;
        }
        .body .top .right .sort {
          color: #bdbcbc;
          font-size: 18px;
        }
        .body .top .right .sort .sort-button {
          color: #ffffff;
          font-size: 18px;
          border: none;
          background: transparent;
        }
      `}</style>
    </section>
  );
};

export default Body;
