import Header from "./Header";
import Footer from "./Footer";
const Layout = (props) => {
  return (
    <div className="layout">
      <Header />
      <div className="layout">{props.children}</div>
      <Footer />
      <style jsx>{`
        .layout {
          min-height: 100vh;
          display: flex;
          flex-direction: column;
        }
      `}</style>
    </div>
  );
};

export default Layout;
