import "../../public/css/bootstrap.css";

const Body = () => {
  return (
    <section className="body">
      <div className="row top">
        <div className="left col-3">
          <input
            type="text"
            className="search form-control f-cap-r"
            placeholder="Search for an article"
          />
        </div>
        {/* .left */}

        <div className="middle col-7">
          <ul className="body-nav list-unstyled f-ap-r d-flex flex-wrap">
            <li className="body-nav-item active">All</li>
            <li className="body-nav-item">Networking</li>
            <li className="body-nav-item">Business</li>
            <li className="body-nav-item">Men&amp;Mice</li>
            <li className="body-nav-item">Events</li>
            <li className="body-nav-item">Courses</li>
          </ul>
        </div>
        {/* .middle */}

        <div className="right col-2">
          <div className="sort">
            <span className="f-cap-r">Sort by: </span>
            <button
              className="btn sort-button f-cap-b"
              type="button"
              id="dropdownMenuButton"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              Newest <img src="/img/caret-down-grey.svg" className="ml-2" />
            </button>
            <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
              <a className="dropdown-item" href="#">
                Action
              </a>
              <a className="dropdown-item" href="#">
                Another action
              </a>
              <a className="dropdown-item" href="#">
                Something else here
              </a>
            </div>
          </div>
        </div>
        {/* .right */}
      </div>
      {/* .top */}

      <div className="row bottom">
        <div className="left col-3">
          <div>
            <button className="this-weeks-post mb-2 f-ap-m">
              THIS WEEKS POST
            </button>
            <div className="date">February 24th 2020</div>
          </div>
        </div>
        {/* .left */}
        <div className="middle col-7 network-security">
          <div className="d-flex">
            <div className="for-image-gap">
              <div></div>
            </div>
            <div>
              <h4>Network Security</h4>
              <h3>Working from 127.0.0.1</h3>
            </div>
          </div>
          <img src="img/network-security.svg" alt="" style={{ opacity: 0.4 }} />
        </div>
        {/* .middle */}
        <div className="right col-2">
          <div className="read-link">
            Read <img src="/img/caret-right-grey.svg" className="ml-2" />
          </div>
        </div>
        {/* .right */}

        <div className="left col-3">
          <div className="date">February 12th 2020</div>
        </div>
        {/* .left */}
        <div className="middle col-7 cloud">
          <div className="d-flex">
            <div className="for-image-gap">
              <div></div>
            </div>
            <div>
              <h4>Cloud</h4>
              <h3>
                Men&amp;Mice Suite 9.3 feature highlight: Akamai Fast DNS
                integration
              </h3>
            </div>
          </div>
          <img src="img/cloud.svg" alt="" style={{ opacity: 0.4 }} />
        </div>
        {/* .middle */}
        <div className="right col-2">
          <div className="read-link">
            Read <img src="/img/caret-right-grey.svg" className="ml-2" />
          </div>
        </div>
        {/* .right */}

        <div className="left col-3">
          <div className="date">February 2nd 2020</div>
        </div>
        {/* .left */}
        <div className="middle col-7 men-and-mice">
          <div className="d-flex">
            <div className="for-image-gap">
              <div></div>
            </div>
            <div>
              <h4>Men&amp;Mice</h4>
              <h3>Change is our constant; innovation is our tradition</h3>
            </div>
          </div>
          <img src="img/men-and-mice.png" alt="" />
        </div>
        {/* .middle */}
        <div className="right col-2">
          <div className="read-link">
            Read <img src="/img/caret-right-grey.svg" className="ml-2" />
          </div>
        </div>
        {/* .right */}

        <div className="left col-3">
          <div className="date">February 12th 2020</div>
        </div>
        {/* .left */}
        <div className="middle col-7 api-auto-rest">
          <div className="d-flex">
            <div className="for-image-gap">
              <div></div>
            </div>
            <div>
              <h4>
                API<span></span>Automation<span></span>REST
              </h4>
              <h3>IP infrastructure automation in action: the REST(ful) API</h3>
            </div>
          </div>
          <img src="img/api-automation-rest.svg" alt="" />
        </div>
        {/* .middle */}
        <div className="right col-2">
          <div className="read-link">
            Read <img src="/img/caret-right-grey.svg" className="ml-2" />
          </div>
        </div>
        {/* .right */}
      </div>
      {/* .bottom */}

      <style jsx>{`
        .body {
          padding-bottom: 35px;
        }
        .top .left,
        .top .middle,
        .top .right {
          padding: 46px 0;
          border-right: 1px solid #4e4c4d;
        }
        .top .middle {
          padding-left: 20px;
        }
        .top .right {
          padding-left: 25px;
          border: none;
        }
        .top .left .search {
          font-size: 18px;
          color: #bdbcbc;
          background: url(/img/search.svg) no-repeat transparent;
          background-position: left center;
          border: none;
          outline: none;
          padding-left: 35px;
        }
        .top .middle .body-nav {
          padding-top: 7px;
        }
        .top .middle .body-nav-item {
          color: rgba(255, 255, 255, 0.6);
          font-size: 18px;
          margin: 0 20px;
          cursor: pointer;
          position: relative;
        }
        .top .middle .body-nav-item.active {
          color: #ffffff;
        }
        .top .middle .body-nav-item:after {
          content: "";
          position: absolute;
          left: 0;
          bottom: -5px;
          width: 100%;
          height: 2px;
          background: transparent;
          transition: all 0.5s;
        }
        .top .middle .body-nav-item.active:after,
        .top .middle .body-nav-item:hover:after {
          background: #ffdb00;
        }
        .top .right .sort {
          color: #bdbcbc;
          font-size: 18px;
        }
        .top .right .sort .sort-button {
          color: #ffffff;
          font-size: 18px;
          border: none;
          background: transparent;
        }
        .bottom .left,
        .bottom .middle,
        .bottom .right {
          border-top: 1px solid #4e4c4d;
          display: flex;
          flex-direction: column;
          justify-content: center;
          height: 358px;
        }
        .bottom .left {
          padding: 0;
        }
        .bottom .right {
          text-align: center;
        }
        .bottom .left,
        .bottom .middle {
          border-right: 1px solid #4e4c4d;
        }
        .bottom .left button.this-weeks-post {
          border: none;
          height: 29px;
          background-color: #ffdb00;
          color: #221f20;
          font-size: 12px;
        }
        .bottom .left .date {
          font-family: "Capitolium Regular";
          color: #b7b6b6;
          font-size: 20px;
        }

        .bottom .middle {
          padding-right: 80px;
          overflow: hidden;
          padding-left: 0;
          position: relative;
        }
        .bottom .middle .for-image-gap div {
          width: 250px;
          height: 100%;
        }
        .bottom .middle img {
          position: absolute;
          left: -58px;
        }
        .bottom .middle h4 {
          font-family: "Apercu Regular";
          font-size: 12px;
          color: #ffdb00;
          text-transform: uppercase;
        }
        .bottom .middle h4 span {
          display: inline-block;
          width: 20px;
        }
        h3 {
          font-family: "Apercu Bold";
          font-size: 36px;
          color: #ffffff;
        }
        .bottom .right .read-link {
          font-family: "Capitolium Bold";
          font-size: 20px;
          color: #b7b6b6;
          cursor: pointer;
        }
        .bottom .right .read-link img {
          transition: all 0.3s;
        }
        .bottom .right .read-link:hover img {
          transform: translate(20px);
        }
      `}</style>
    </section>
  );
};

export default Body;
