const Body = () => {
  return (
    <section className="body">
      <div className="top">
        <div className="left">
          <input
            type="text"
            className="search form-control"
            placeholder="Search for an article"
          />
        </div>
        {/* /.left */}
        <div className="middle">
          <ul className="body-nav list-unstyled f-cap-r d-flex">
            <li className="body-nav-item">All</li>
            <li className="body-nav-item">Networking</li>
            <li className="body-nav-item">Business</li>
            <li className="body-nav-item">Men&amp;Mice</li>
            <li className="body-nav-item">Events</li>
            <li className="body-nav-item">Courses</li>
          </ul>
          {/* /.body-nav */}
        </div>
        {/* /.moddle */}
        <div className="right">
          <div className="sort">
            <span>Sort by: </span>
            <button className="sort-button">Newest</button>
          </div>
        </div>
        {/* /.right */}
      </div>
      {/* /.top */}
      <div className="bottom">
        <div className="left"></div>
        {/* /.left */}
        <div className="middle"></div>
        {/* /.moddle */}
        <div className="right"></div>
        {/* /.right */}
      </div>
      {/* /.bottom */}
      <style jsx>{`
        textarea:hover,
        input:hover,
        textarea:active,
        input:active,
        textarea:focus,
        input:focus,
        button:focus,
        button:active,
        button:hover,
        label:focus,
        .btn:active,
        .btn.active {
          outline: 0px !important;
          -webkit-appearance: none;
          box-shadow: none !important;
        }
        .f-ap-r {
          font-family: "Apercu Regular";
        }
        .f-ap-m {
          font-family: "Apercu Medium";
        }
        .f-ap-b {
          font-family: "Apercu Bold";
        }
        .f-cap-r {
          font-family: "Capitolium Regular";
        }
        .f-cap-b {
          font-family: "Capitolium Bold";
        }
      `}</style>
      <style jsx>{`
        .body .top .left .search {
          font-size: 18px;
          color: #bdbcbc;
          background: url(/img/search.svg) no-repeat transparent;
          background-position: left center;
          border: none;
          outline: none;
          padding-left: 35px;
        }
        .body .top .middle .body-nav-item {
          color: rgba(255, 255, 255, 0.6);
        }
      `}</style>
    </section>
  );
};

export default Body;
